extern alias BigIntegerExtensionsAssembly;
using System.Globalization;
using System.Numerics;
using BigIntegerExtensionsAssembly::Singulink.Numerics;
using Microsoft.VisualStudio.TestTools.UnitTesting;
using Shouldly;

namespace Singulink.Numerics.BigIntegerExtensions.Tests;

[TestClass]
public class CountTests
{
    [DataTestMethod]
    [DataRow("0", 1, 0)]
    [DataRow("1", 1, 0)]
    [DataRow("-1", 1, 0)]
    [DataRow("9", 1, 0)]
    [DataRow("-9", 1, 0)]
    [DataRow("10", 2, 1)]
    [DataRow("-10", 2, 1)]
    [DataRow("99,999,999", 8, 0)]
    [DataRow("-99,999,999", 8, 0)]
    [DataRow("100,000,000", 9, 8)]
    [DataRow("-100,000,000", 9, 8)]
    [DataRow("100,000,001", 9, 0)]
    [DataRow("-100,000,001", 9, 0)]
    [DataRow("123,456,780,000", 12, 4)]
    [DataRow("-123,456,780,000", 12, 4)]
    [DataRow("123,456,789,000", 12, 3)]
    [DataRow("-123,456,789,000", 12, 3)]
    [DataRow("12,345,768,912,345,678,910", 20, 1)]
    [DataRow("-12,345,768,912,345,678,910", 20, 1)]
    [DataRow("123,456,789,012,345,678,901,234,567,890,123,456,000", 39, 3)]
    [DataRow("-123,456,789,012,345,678,901,234,567,890,123,456,000", 39, 3)]
    [DataRow("", 1000, 0)]
    [DataRow("-", 1000, 0)]
    [DataRow("", 1001, 1000)]
    [DataRow("-", 1001, 1000)]
    [DataRow("", 1001, 0)]
    [DataRow("-", 1001, 0)]
    [DataRow("", 1001, 6)]
    [DataRow("-", 1001, 6)]
    public void CountDigitsAndTrailingZeros(string stringValue, int digits, int trailingZeros)
    {
        var value = BigInteger.Parse(stringValue, NumberStyles.Number, CultureInfo.InvariantCulture);

        value.CountDigits().ShouldBe(digits);
        value.CountDigitsAndTrailingZeros().ShouldBe((digits, trailingZeros));
    }
}